// based on PHP Name Parser by Josh Fraser (joshfraser.com)
// http://www.onlineaspect.com/2009/08/17/splitting-names/
// ported to JavaScript by Mark Pemburn (pemburnia.com)
// released under Apache 2.0 license

var NameParser = (function(){
    function NameParse() {
        return NameParse;
    }

    // split full names into the following parts:
    // - prefix / salutation  (Mr., Mrs., etc)
    // - given name / first name
    // - middle initials
    // - surname / last name
    // - suffix (II, Phd, Jr, etc)
    NameParse.parse = function (fullast_name) {
        fullast_name = fullast_name.trim();

        var nameParts = [];
        var last_name = "";
        var first_name = "";
        var initials = "";
        var word = null;
        var j = 0;
        var i = 0;

        // split into words
        // completely ignore any words in parentheses
        nameParts = fullast_name.split(" ").filter(function(namePart){
            return (namePart.indexOf("(") === -1);
        });

        var numWords = nameParts.length;
        // is the first word a title? (Mr. Mrs, etc)
        var salutation = this.is_salutation(nameParts[0]);
        var suffix = this.is_suffix(nameParts[numWords - 1]);
        // set the range for the middle part of the name (trim prefixes & suffixes)
        var start = (salutation) ? 1 : 0;
        var end = (suffix) ? numWords - 1 : numWords;

        word = nameParts[start];
        // if we start off with an initial, we'll call it the first name
        if (this.is_initial(word)) {
            // if so, do a look-ahead to see if they go by their middle name
            // for ex: "R. Jason Smith" => "Jason Smith" & "R." is stored as an initial
            // but "R. J. Smith" => "R. Smith" and "J." is stored as an initial
            if (this.is_initial(nameParts[start + 1])) {
                first_name += " " + word.toUpperCase();
            } else {
                initials += " " + word.toUpperCase();
            }
        } else {
            first_name += " " + this.fix_case(word);
        }

        // concat the first name
        for (i=start + 1; i<(end - 1); i++) {
            word = nameParts[i];
            // move on to parsing the last name if we find an indicator of a compound last name (Von, Van, etc)
            // we do not check earlier to allow for rare cases where an indicator is actually the first name (like "Von Fabella")
            if (this.is_compound_last_name(word)) {
                break;
            }

            if (this.is_initial(word)) {
                initials += " " + word.toUpperCase();
            } else {
                first_name += " " + this.fix_case(word);
            }
        }

        // check that we have more than 1 word in our string
        if ((end - start) > 1) {
            // concat the last name
            for (j=i; j<end; j++) {
                last_name += " " + this.fix_case(nameParts[j]);
            }
        }

        // return the various parts in an array
        return {
            "salutation": salutation || "",
            "first": first_name.trim(),
            "initials": initials.trim(),
            "last": last_name.trim(),
            "suffix": suffix || ""
        };
    };

    NameParse.removeIgnoredChars = function (word) {
        //ignore periods
        return word.replace(".","");
    };

    // detect and format standard salutations
    // I'm only considering english honorifics for now & not words like
    NameParse.is_salutation = function (word) {
        word = this.removeIgnoredChars(word).toLowerCase();
        // returns normalized values
        if (word === "mr" || word === "master" || word === "mister") {
            return "Mr.";
        } else if (word === "mrs") {
            return "Mrs.";
        } else if (word === "miss" || word === "ms") {
            return "Ms.";
        } else if (word === "dr") {
            return "Dr.";
        } else if (word === "rev") {
            return "Rev.";
        } else if (word === "fr") {
            return "Fr.";
        } else {
            return false;
        }
    };

    //  detect and format common suffixes
    NameParse.is_suffix = function (word) {
        word = this.removeIgnoredChars(word).toLowerCase();
        // these are some common suffixes - what am I missing?
        var suffixArray = [
            'I','II','III','IV','V','Senior','Junior','Jr','Sr','PhD','APR','RPh','PE','MD','MA','DMD','CME',
            "BVM","CFRE","CLU","CPA","CSC","CSJ","DC","DD","DDS","DO","DVM","EdD","Esq",
            "JD","LLD","OD","OSB","PC","Ret","RGS","RN","RNC","SHCJ","SJ","SNJM","SSMO",
            "USA","USAF","USAFR","USAR","USCG","USMC","USMCR","USN","USNR"
        ];

        var suffixIndex = suffixArray.map(function(suffix){
            return suffix.toLowerCase();
        }).indexOf(word);

        if(suffixIndex >= 0) {
            return suffixArray[suffixIndex];
        } else {
            return false;
        }
    };

    // detect compound last names like "Von Fange"
    NameParse.is_compound_last_name = function (word) {
        word = word.toLowerCase();
        // these are some common prefixes that identify a compound last names - what am I missing?
        var words = ['vere','von','van','de','del','della','di','da','pietro','vanden','du','st.','st','la','lo','ter', 'o', "o'", 'mac', 'fitz'];
        return (words.indexOf(word) >= 0);
    };

    // single letter, possibly followed by a period
    NameParse.is_initial = function (word) {
        if (!word) { return false; }
        word = this.removeIgnoredChars(word);
        return (word.length === 1);
    };

    // detect mixed case words like "McDonald"
    // returns false if the string is all one case
    NameParse.is_camel_case = function (word) {
        var ucReg = /[A-Z]+/;
        var lcReg = /[a-z]+/;
        return (ucReg.exec(word) && lcReg.exec(word));
    };

    // ucfirst words split by dashes or periods
    // ucfirst all upper/lower strings, but leave camelcase words alone
    NameParse.fix_case = function (word) {
        // uppercase words split by dashes, like "Kimura-Fay"
        word = this.safe_ucfirst("-",word);
        // uppercase words split by periods, like "J.P."
        word = this.safe_ucfirst(".",word);
        return word;
    };

    // helper for this.fix_case
    // uppercase words split by the seperator (ex. dashes or periods)
    NameParse.safe_ucfirst = function (seperator, word) {
        return word.split(seperator).map(function(thisWord){
            if(this.is_camel_case(thisWord)) {
                return thisWord;
            } else {
                return thisWord.substr(0,1).toUpperCase() + thisWord.substr(1).toLowerCase();
            }
        }, this).join(seperator);
    };

    return NameParse;
})();

module.exports = NameParser;

'use strict';
const crypto = require('crypto');

const SALT_LENGTH = 64; // Length of the salt, in bytes
const TOKEN_LENGTH = 64;
const HASH_LENGTH = 64; // Length of the hash, in bytes
const HASH_ITERATIONS = 1000; // Number of pbkdf2 iterations
const IV_LENGTH = 16; // For AES, this is always 16


function generateToken() {
  return new Promise((resolve, reject) => {
    crypto.randomBytes(TOKEN_LENGTH, (err, token) => {
      if(err) reject(err);
      resolve(token);
    })
  });
}

function generateSalt() {
  return new Promise((resolve, reject) => {
    crypto.randomBytes(SALT_LENGTH, (err, salt) => {
      if(err) reject(err);
      resolve(salt);
    })
  });
}

function hash(data, salt) {
  return new Promise((resolve, reject) => {
    crypto.pbkdf2(data, salt, HASH_ITERATIONS, HASH_LENGTH, 'sha1', (err, hash) => {
      if (err) {
        reject(err);
      }
      resolve({
        salt: salt,
        hash: hash
      });
    });
  });
}

function saltAndHash(data) {
  return generateSalt().then((salt) => {
    return hash(data, salt);
  });
}

function encrypt(text, ENCRYPTION_KEY) {
  let iv = crypto.randomBytes(IV_LENGTH);
  let cipher = crypto.createCipheriv('aes-256-cbc', Buffer.from(ENCRYPTION_KEY), iv);
  let encrypted = cipher.update(text);

  encrypted = Buffer.concat([encrypted, cipher.final()]);

  return iv.toString('hex') + ':' + encrypted.toString('hex');
}

function decrypt(text, ENCRYPTION_KEY) {
  let textParts = text.split(':');
  let iv = Buffer.from(textParts.shift(), 'hex');
  let encryptedText = Buffer.from(textParts.join(':'), 'hex');
  let decipher = crypto.createDecipheriv('aes-256-cbc', Buffer.from(ENCRYPTION_KEY), iv);
  let decrypted = decipher.update(encryptedText);

  decrypted = Buffer.concat([decrypted, decipher.final()]);

  return decrypted.toString();
}

module.exports = { 
  encrypt,
  decrypt,
  generateSalt, 
  generateToken, 
  hash, 
  saltAndHash };

const JWT = require('jsonwebtoken');
const Secure = require('./secure');

class Token {
    constructor({app_secret}, options){
        if(!app_secret)
            throw new Error('No app secret was sent.');
        this.secret = app_secret;
    }

    generate(data, jwt_duration) {
        if(!this.secret)
            throw new Error('App Secret not defined');
        
        return JWT.sign({
            data: Secure.encrypt(JSON.stringify({
                user_id: data.user_id
            }), this.secret)
        }, this.secret, {
            expiresIn: jwt_duration
        });
    }

    validate(token) {
        var me = this;
        if(!this.secret)
            throw new Error('App Secret not defined');

        return new Promise((resolve, reject) => {
            JWT.verify(token, me.secret, (err, decoded) => {
                if (err) {
                    reject(err);
                }
                let data = decoded.data || false;
                if(data){
                    data = JSON.parse(Secure.decrypt(data, me.secret))
                }
                resolve({data,decoded});
            });
        })
    }
}

module.exports = Token;

